export default function Skills() {
  const skillCategories = [
    {
      title: "Programming Languages",
      colorClass: "text-blue-600",
      bgClass: "bg-blue-100 text-blue-800",
      skills: ['Java', 'Python', 'React Native']
    },
    {
      title: "Web Development",
      colorClass: "text-blue-600",
      bgClass: "bg-green-100 text-green-800",
      skills: ['HTML', 'CSS', 'JavaScript', 'ReactJS', 'Express']
    },
    {
      title: "Databases",
      colorClass: "text-blue-600",
      bgClass: "bg-purple-100 text-purple-800",
      skills: ['SQL', 'MongoDB']
    },
    {
      title: "Tools & Platforms",
      colorClass: "text-blue-600",
      bgClass: "bg-orange-100 text-orange-800",
      skills: ['NetBeans', 'Jupyter Notebook', 'Kaggle', 'Google Colab', 'VS Code', 'Visual Studio']
    }
  ];

  const certifications = [
    'Cyber Security (Microsoft)',
    'Robotic Process Automation (RPA)',
    'Advanced Cyber Security (Microsoft)',
    'Unity Game Development',
    'Tableau Data Visualization',
    'Ruby on Rails'
  ];

  return (
    <section id="skills" className="py-16 bg-slate-50">
      <div className="container mx-auto px-4 md:px-8">
        <h2 className="text-3xl font-bold text-center mb-12">My Skills</h2>
        
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {skillCategories.map((category, index) => (
            <div key={index} className="bg-white rounded-lg shadow-md p-6 hover:shadow-lg transition-shadow duration-300">
              <h3 className={`text-xl font-semibold mb-4 ${category.colorClass}`}>{category.title}</h3>
              <div className="flex flex-wrap gap-2">
                {category.skills.map(skill => (
                  <span key={skill} className={`${category.bgClass} px-3 py-1 rounded-full text-sm`}>
                    {skill}
                  </span>
                ))}
              </div>
            </div>
          ))}
        </div>
        
        <div className="mt-12">
          <h3 className="text-xl font-semibold mb-6 text-center">Certifications</h3>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
            {certifications.map((cert, index) => (
              <div key={index} className="bg-white rounded-lg shadow-md p-4 flex items-center">
                <div className="w-3 h-3 bg-blue-600 rounded-full mr-3"></div>
                <span className="text-slate-700">{cert}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
}
