import { Mail, Phone, Linkedin, Github, Send, MapPin, MessageSquare } from 'lucide-react';
import { useRef, useState } from 'react';

export default function Contact() {
  const nameRef = useRef();
  const emailRef = useRef();
  const messageRef = useRef();
  const [formStatus, setFormStatus] = useState({ type: null, message: null });
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setFormStatus({ type: null, message: null });

    const name = nameRef.current.value.trim();
    const email = emailRef.current.value.trim();
    const message = messageRef.current.value.trim();

    if (!name || !email || !message) {
      setFormStatus({ type: 'error', message: "Please fill out all fields." });
      setLoading(false);
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/send-email", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, email, message }),
      });

      const data = await res.json();
      if (res.ok) {
        setFormStatus({ type: 'success', message: "Message sent successfully! I'll get back to you soon." });
        nameRef.current.value = "";
        emailRef.current.value = "";
        messageRef.current.value = "";
      } else {
        setFormStatus({ type: 'error', message: data.error || "Failed to send message." });
      }
    } catch (err) {
      console.error("Email send error:", err);
      setFormStatus({ type: 'error', message: "Something went wrong. Please try again later." });
    } finally {
      setLoading(false);
    }
  };

  return (
    <section id="contact" className="py-20 bg-gradient-to-b from-white to-blue-50">
      <div className="container mx-auto px-4 md:px-8 max-w-6xl">
        <div className="text-center mb-16">
          <h2 className="text-4xl font-bold mb-4 text-slate-800">Get In Touch</h2>
          <div className="w-20 h-1 bg-blue-600 mx-auto"></div>
          <p className="mt-4 text-slate-600 max-w-2xl mx-auto">
            Have a question or want to work together? I'd love to hear from you!
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Contact Info Cards */}
          <div className="space-y-6">
            <div className="bg-white rounded-xl shadow-md border border-slate-100 p-6 transition-all duration-300 hover:shadow-lg hover:border-blue-200">
              <div className="flex items-center mb-4">
                <div className="p-3 rounded-lg bg-blue-100 mr-4">
                  <Mail className="w-6 h-6 text-blue-600" />
                </div>
                <h3 className="text-xl font-bold text-slate-800">Email</h3>
              </div>
              <a 
                href="mailto:kushalgupta8424@gmail.com" 
                className="text-slate-600 hover:text-blue-600 transition-colors duration-300 block"
              >
                kushalgupta8424@gmail.com
              </a>
            </div>

            <div className="bg-white rounded-xl shadow-md border border-slate-100 p-6 transition-all duration-300 hover:shadow-lg hover:border-blue-200">
              <div className="flex items-center mb-4">
                <div className="p-3 rounded-lg bg-blue-100 mr-4">
                  <Phone className="w-6 h-6 text-blue-600" />
                </div>
                <h3 className="text-xl font-bold text-slate-800">Phone</h3>
              </div>
              <a 
                href="tel:+918424813828" 
                className="text-slate-600 hover:text-blue-600 transition-colors duration-300 block"
              >
                (+91) 8424813828
              </a>
            </div>

            <div className="bg-white rounded-xl shadow-md border border-slate-100 p-6 transition-all duration-300 hover:shadow-lg hover:border-blue-200">
              <div className="flex items-center mb-4">
                <div className="p-3 rounded-lg bg-blue-100 mr-4">
                  <MapPin className="w-6 h-6 text-blue-600" />
                </div>
                <h3 className="text-xl font-bold text-slate-800">Location</h3>
              </div>
              <p className="text-slate-600">
                Mumbai, Maharashtra, India
              </p>
            </div>

            <div className="bg-white rounded-xl shadow-md border border-slate-100 p-6">
              <h3 className="text-xl font-bold text-slate-800 mb-4">Social Profiles</h3>
              <div className="flex space-x-3">
                <a 
                  href="https://www.linkedin.com/in/kushal-gupta/" 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="p-3 rounded-lg bg-blue-100 text-blue-600 hover:bg-blue-200 transition-colors duration-300"
                >
                  <Linkedin className="w-6 h-6" />
                </a>
                <a 
                  href="https://github.com/kushal228030" 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="p-3 rounded-lg bg-slate-100 text-slate-700 hover:bg-slate-200 transition-colors duration-300"
                >
                  <Github className="w-6 h-6" />
                </a>
                <a 
                  href="mailto:kushalgupta8424@gmail.com"
                  className="p-3 rounded-lg bg-red-100 text-red-600 hover:bg-red-200 transition-colors duration-300"
                >
                  <Mail className="w-6 h-6" />
                </a>
              </div>
            </div>
          </div>

          {/* Contact Form */}
          <div className="lg:col-span-2">
            <div className="bg-white rounded-xl shadow-md border border-slate-100 p-8">
              <div className="flex items-center mb-6">
                <div className="p-3 rounded-lg bg-blue-100 mr-4">
                  <MessageSquare className="w-6 h-6 text-blue-600" />
                </div>
                <h3 className="text-2xl font-bold text-slate-800">Send Me a Message</h3>
              </div>

              {formStatus.message && (
                <div className={`mb-6 p-4 rounded-lg ${
                  formStatus.type === 'success' 
                    ? 'bg-green-100 text-green-800 border border-green-200' 
                    : 'bg-red-100 text-red-800 border border-red-200'
                }`}>
                  {formStatus.message}
                </div>
              )}

              <form className="space-y-6" onSubmit={handleSubmit}>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <label htmlFor="name" className="block text-sm font-medium text-slate-700 mb-2">Full Name</label>
                    <input 
                      type="text"
                      id="name"
                      ref={nameRef}
                      className="w-full px-4 py-3 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-300"
                      placeholder="John Doe"
                    />
                  </div>

                  <div>
                    <label htmlFor="email" className="block text-sm font-medium text-slate-700 mb-2">Email Address</label>
                    <input 
                      type="email"
                      id="email"
                      ref={emailRef}
                      className="w-full px-4 py-3 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-300"
                      placeholder="john@example.com"
                    />
                  </div>
                </div>

                <div>
                  <label htmlFor="message" className="block text-sm font-medium text-slate-700 mb-2">Your Message</label>
                  <textarea 
                    id="message"
                    ref={messageRef}
                    rows="6"
                    className="w-full px-4 py-3 border border-slate-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-300"
                    placeholder="I'd like to discuss a potential project..."
                  ></textarea>
                </div>

                <button 
                  type="submit" 
                  disabled={loading}
                  className={`flex items-center justify-center gap-2 bg-blue-600 hover:bg-blue-700 text-white font-medium py-3 px-6 rounded-lg transition-colors duration-300 w-full ${loading ? 'opacity-70 cursor-not-allowed' : ''}`}
                >
                  {loading ? (
                    <>
                      <div className="w-5 h-5 border-t-2 border-b-2 border-white rounded-full animate-spin"></div>
                      <span>Sending...</span>
                    </>
                  ) : (
                    <>
                      <Send className="w-5 h-5" />
                      <span>Send Message</span>
                    </>
                  )}
                </button>
              </form>

              <div className="mt-6 text-center text-slate-500 text-sm">
                I'll get back to you as soon as possible. Thanks for reaching out!
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}
